"use strict";angular.module("ngQueensApp",["ngAnimate","ngCookies"]),angular.module("ngQueensApp").controller("MainController",["$scope",function(a){var b=this;b.currentGrid=4,b.gridSizes=[{name:"4x4",value:4},{name:"5x5",value:5},{name:"6x6",value:6},{name:"7x7",value:7},{name:"8x8",value:8}],b.getNumber=function(a){for(var b=[],c=1;a>=c;c++)b.push(c);return b},b.changeGridSize=function(a){b.currentGrid=a},a.$watch(function(a){return b.currentGrid},function(a,b){})}]),angular.module("ngQueensApp").directive("board",function(){return{restrict:"E",templateUrl:"views/board.html"}}),angular.module("ngQueensApp").run(["$templateCache",function(a){a.put("views/board.html",'<div ng-repeat="i in ctrl.getNumber(ctrl.currentGrid) track by i" class="board-row"> <div ng-repeat="j in ctrl.getNumber(ctrl.currentGrid) track by j" id="{{i}}{{j}}" class="tile-{{ctrl.currentGrid}}"> </div> </div> <img ng-repeat="i in ctrl.getNumber(ctrl.currentGrid) track by i" src="images/queen.3cf9eb09.png" id="Q{{i}}" class="queen-{{ctrl.currentGrid}}">'),a.put("views/main.html",'<div id="content" ng-controller="MainController as ctrl"> <div id="heading" class="row"> <h1 class="title">ngQueens</h1> </div> <div id="instructions" class="row"> Place all the queens on the board so that <br> no two queens threaten each other ! <button class="restart-button">New game</button> <div class="btn-group"> <button type="button" class="dropdown-toggle grid-button" data-toggle="dropdown"> Grid </button> <ul class="dropdown-menu"> <li><a ng-repeat="item in ctrl.gridSizes" ng-click="ctrl.changeGridSize(item.value)">{{ item.name }}</a></li> </ul> </div> </div> <div id="board-container"> <board></board> </div> </div>')}]);